<?php

namespace QuizBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * AnswersRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class AnswersRepository extends EntityRepository
{

    public function findById($id) {
        return $this->getEntityManager()
            ->createQuery(
                'SELECT r.status FROM QuizBundle:Answers r 
                      WHERE r.id = :id'
            )
            ->setParameter('id', $id)
            ->getResult();
    }

    public function getAllByIdQuestions($id)
    {
        return $this->getEntityManager()
            ->createQuery(
                'SELECT r FROM QuizBundle:Answers r
                      INNER JOIN QuizBundle:Questions q WITH r.idQuestion = q.id 
                      INNER JOIN QuizBundle:Themes t WITH q.idTheme = t.id 
                      WHERE t.id = :id'
            )
            ->setParameter('id', $id)
            ->getResult();
    }
}
